import React, {useState} from 'react'
import { useNavigate } from 'react-router-dom';

function Login() {

    // const navigate = useNavigate();

    // const [email, setEmail] = useState(myEmail);
    // const [password, setPassword] = useState(myPassword);
    // const [error, setError] = useState(false);

    // const handleLogin = (e)=>{
    //     e.preventDefault();

    //     if(email !== myEmail || password !== myPassword){
    //         setError(true);
    //     }
    //     else{
    //         setIsLoggedIn(true);
    //         navigate('/');
    //     }
    // }

  return (
    <div className="container main-container">
        <form className='login' onSubmit={handleLogin}>
        <h2>Login</h2>
        <div className="social-logins">
            <div className="facebook social-login">
                <i class="fa-brands fa-facebook-f"></i>
                <span>Login with Facebook</span>
            </div>
            <div className="apple social-login">
                <i class="fa-brands fa-apple"></i>
                <span>Login with Apple</span>
            </div>
            <div className="google social-login">
                <i class="fa-brands fa-google"></i>
                <span>Login with Google</span>
            </div>
        </div>
        <div className="or">
            <hr />
            <span>OR</span>
            <hr />
        </div>
        <div className="user-login">
            <div className="input-container">
                <label htmlFor="email">Email</label>
                <input type="email" id='email' placeholder='Email here' value={email} onChange={(e)=>setEmail(e.target.value)} required />
            </div>
            <div className="input-container">
                <label htmlFor="password">Password</label>
                <input type="password" id='password' placeholder='Password here' value={password} onChange={(e)=>setPassword(e.target.value)} required />
            </div>
        </div>
        <button className='login-btn' type='submit' >Login</button>
        {
            error? <p>Incorrect email or password</p> : null
        }
    </form>
    </div>
  )
}

export default Login
